import { useState, useEffect } from "react";
import api from "../../api/api";
import SearchBar from "../../components/SearchBar";
import TopBar from "../../components/TopBar";
import Pagination from "../../components/Pagination";
import Loader from "../../components/Loader";
import { isLogin } from "../../util/common";
import { Link } from 'react-router-dom';

const WriteManagement = () =>{
  const [lists, setLists] = useState('');
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(1);
  const offset = (page - 1) * limit;
  const [pageStart, setPageStart] = useState(0);

  const [loading, setLoading] = useState(false);

  // 체크된 아이템을 담을 배열
  const [checkItems, setCheckItems] = useState([]);

  const [inputSearch, setInputSearch] = useState('');

  const onChangeSearch = (e) =>{
    setInputSearch(e.target.value);
  }

  useEffect(() => {
    const fetchData = async () => {
     setLoading(true);
      try {
        const response = await api.writeInfo();
        setLists(response.data);
      } catch (e) {
        console.log(e);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  if(!isLogin){
    window.location.replace("/");
  }
  
  if(loading) {
    return <div className="center"><Loader/></div>
  }
  
  // 체크박스 단일 선택
  const handleSingleCheck = (checked, id) => {
    if (checked) {
      // 단일 선택 시 체크된 아이템을 배열에 추가
      setCheckItems(prev => [...prev, id]);
    } else {
      // 단일 선택 해제 시 체크된 아이템을 제외한 배열 (필터)
      setCheckItems(checkItems.filter((el) => el !== id));
    }
  };

  // 체크박스 전체 선택
  const handleAllCheck = (checked) => {
    if(checked) {
      // 전체 선택 클릭 시 데이터의 모든 아이템(id)를 담은 배열로 checkItems 상태 업데이트
      const idArray = [];
      lists.slice(offset, offset + limit).forEach((el) => {idArray.push(el.writeNum)});
      setCheckItems(idArray);
    }
    else {
      // 전체 선택 해제 시 checkItems 를 빈 배열로 상태 업데이트
      setCheckItems([]);
    }
  }

  const writeDelete = () =>{
    window.localStorage.setItem("target", checkItems);
    const fetchDeleteData = async () => {
      setLoading(true);
       try {
         const response = await api.writeDelete();
         setLists(response.data);
       } catch (e) {
         console.log(e);
       }
       setLoading(false);
     };
    fetchDeleteData();
    setCheckItems([]);
  }
  if(isLogin){
    return(
      <div className="center">
        <TopBar name="게시글 관리" high1="콘텐츠 관리"/>
        <div className="searchBar">
          <input type="text" placeholder="제목, 작성자" value ={inputSearch} onChange={onChangeSearch}/>
          <button>
            <Link to={`/content/writeManagement/search/${inputSearch}`}>검색</Link>
          </button>
        </div>
        <div>
        <label className="pageselect">
            페이지 당 표시할 게시물 수:&nbsp;
            <select
              type="number"
              value={limit}
              onChange={({ target: { value } }) => {
                setLimit(Number(value));
                setPage(1);
                setPageStart(0);
              }}
            >
              <option value="5">5</option>
              <option value="7">7</option>
              <option value="10" selected>10</option>
              <option value="12">12</option>
              <option value="20">20</option>
            </select>
          </label>
          <div className="tableWrapper">
            <table>
              <thead>
                <tr>
                  <input type='checkbox' name='select-all'
                    onChange={(e) => handleAllCheck(e.target.checked)}
                    // 데이터 개수와 체크된 아이템의 개수가 다를 경우 선택 해제 (하나라도 해제 시 선택 해제)
                    checked={checkItems.length === (
                    Math.floor(lists.length/limit) >= page ? 
                    limit :lists.length % limit)? true : false} 
                  />
                  <th>게시글 번호</th>
                  <th>게시글 제목</th>
                  <th>작성자</th>
                  <th>작성일</th>
                </tr>
              </thead>
              <tbody>
                {lists &&
                  lists.slice(offset, offset + limit)
                  .map(({ writeNum, writeName, writeDate, nickname}) => (
                    <tr key={writeNum}>
                      <td>
                      <input type='checkbox' 
                        name={`select-${writeNum}`}
                        onChange={(e) => handleSingleCheck(e.target.checked, writeNum)}
                        // 체크된 아이템 배열에 해당 아이템이 있을 경우 선택 활성화, 아닐 시 해제
                        checked={checkItems.includes(writeNum) ? true : false} 
                        />
                      </td>
                      <td>{writeNum}</td>
                      <td><Link to={`/content/writeManagement/detail/${writeNum}`}>{writeName}</Link></td>
                      <td>{writeDate}</td>
                      <td>{nickname}</td>
                    </tr>
                  ))
                }
              </tbody>
            </table>
            <div className="btnlst">
            <button onClick={writeDelete}>삭제</button>
            </div>
          </div>
          <Pagination
            total={lists.length}
            limit={limit}
            page={page}
            setPage={setPage}
            pageStart={pageStart}
            setPageStart={setPageStart}
            checkItems={checkItems} 
            setCheckItems={setCheckItems}
          />
        </div>
      </div>
    );
  }
};
export default WriteManagement;
